INSERT INTO `ims_han_sheka_zhufu` VALUES ('203', '0', '发送人的名字', '收卡人的名字', '不因换季而不想你，不因路远而不念你，不因忙碌而疏远你，不因时间的冲刷而淡忘你。不管遇到怎么样的困难，不管遇到多大的挫折，人总要活在希望里，要在困境中奋起，在磨难中充满希望，你就是那道温暖的光，带给我可爱的黎明，感谢有你！', '239', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
