INSERT INTO `ims_han_sheka_zhufu` VALUES ('216', '0', '发送人的名字', '收卡人的名字', '除夕之夜包饺子儿，用甜蜜和面，用幸福拌馅儿，用快乐擀皮儿，包入和美，煮出吉祥，捞出福气，咀嚼如意，欢声笑语满桌绽放，祝你候年天天开心，万事顺畅！', '252', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('217', '0', '发送人的名字', '收卡人的名字', '新年第一天，收集我心中的每一份温暖祝福，每一种柔软愿望，描绘我心中每一道多彩细节，每一个深切期盼，寄予你猴年第一声问候！新年快乐！', '253', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('218', '0', '发送人的名字', '收卡人的名字', '元宵节来吃汤圆，吃碗汤圆心甜甜；幸福汤圆一入口，健康快乐常陪伴；爱情汤圆一入口，心如细丝甜如蜜；金钱汤圆一入口，财源滚滚斩不断！元宵快乐！', '254', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('219', '0', '发送人的名字', '收卡人的名字', '春节到，向您问声好；福虎啸，票房步步高；梅霜傲，影院乐逍遥；鞭炮闹，黑马金光耀；枝头噪，喜鹊把春报；祝您新春快乐，马年吉祥！', '255', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '1', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('220', '0', '发送人的名字', '收卡人的名字', '在缤纷焰火和欢快乐曲中，新的一年向我们走来。辞旧迎新之际，票房随观众呼声高涨；祝福随春风一起送上，马年好运挡不住，黑马奔腾滚滚来！新年快乐！', '256', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('221', '0', '发送人的名字', '收卡人的名字', '笑容，在花间绽放；温馨，沐浴着阳光；梦想，在坚持中实现；机遇，在守候中到来；烦恼，在烟云中飘散；情谊，在问候中酝酿；幸福，在新年许愿中来临。新年快乐！', '257', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('222', '0', '发送人的名字', '收卡人的名字', '春暖花开迎幸福，好运连连乐开怀。新年奏起欢快的乐曲，回想过去一年的欢笑与汗水，感谢有你同行，祝今后的每一天开开心心，快乐活色生香，幸福有滋有味，家庭和和美美，万事鸿运当头，新年快乐！', '258', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '1', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('223', '0', '发送人的名字', '收卡人的名字', '聚喜马拉雅之高度，拢天涯海角之东风，携五湖四海之财气，借辞旧迎新之好运，乘万象更新之祝福，愿您在新的一年大吉大利，一马当先！事业红红火火，家庭和和美美，财运路路畅通，人气节节攀升，新年快乐！', '259', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '1', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('224', '0', '发送人的名字', '收卡人的名字', '青春像是一个五光十色，充满能量的大魔方；不断诱惑着我们去探寻，因为我们的未来不可限量；我们拥有一本属于自己的青春纪念册！', '260', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('225', '0', '发送人的名字', '收卡人的名字', '爱你爱不够，只想多留一刻在你左右，心跳的声音是爱的美丽旋律，让我们的爱奏出世界上最动听的音符!亲爱的，我爱你！', '261', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('226', '0', '发送人的名字', '收卡人的名字', '元宵节要到了，祝愿您的家庭像汤圆一样“团团圆圆”，爱情像汤圆一样“甜甜蜜蜜”，事业像汤圆一样“越滚越大”，财源像煮汤圆一样“滚滚而来”，朋友像汤圆一样“实实在在”。祝朋友你元宵节甜甜美美，圆圆满满！', '262', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('227', '0', '发送人的名字', '收卡人的名字', '每个生灵的诞生都给这个多彩的世界添加了一道颜色，而你是最亮丽的一笔。祝你生日快乐！', '263', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('228', '0', '发送人的名字', '收卡人的名字', '祝你猴年前程一个筋斗十万八千里，兼七十二变混世才学，具八十一种创业本领。', '264', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('229', '0', '发送人的名字', '收卡人的名字', '祝你猴年：新年大吉大利、百無禁忌、五福臨門、富貴吉祥、橫財就手、財運亨通、步步高升、生意興隆、東成西就、恭喜發財!', '265', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('230', '0', '发送人的名字', '亲爱的朋友', '许一个美好的心愿祝你圣诞快乐连连，送一份美妙的感觉祝你圣诞万事圆圆，送一份漂亮的礼物祝你微笑甜甜。', '266', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('231', '0', '发送人的名字', '收卡人的名字', '送你一份春节美食，主要成分：真心、思念、快乐；特别营养：温馨、幸福；制造商：你的朋友；有效期：一生。祝万事如意、合家欢乐！', '267', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('232', '0', '发送人的名字', '收卡人的名字', '雪下完了，留下炸鸡和啤酒。雪人化了，留下和你一起堆雪人的我，只想轻轻告诉你：春天来了，都敏俊和二千在外星球愉快的玩耍，给我留下唯一的超能力就是奋不顾身的保护你，只要你开心，一年四季都是初雪的心情。', '268', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '1', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('233', '0', '发送人的名字', '亲爱的朋友', '这位同学，无论是否情愿，该发生的事情终究会发生，地球人把这种事称之为命运。叫兽说过报告必须按格式写，字数不能少，复制粘贴这些全都不能干喔。千颂伊已经得了0分和叫兽的心，都敏俊不敢来救你。还好，你有我！！！人家永远支持你！！！', '269', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('234', '0', '发送人的名字', '收卡人的名字', '不想开学，不想上班，不想恋爱，不想结婚，不想洗头，不想出门，不想见陌生人……叫兽！！你听到了吗？？要么带我走，要么给我炸鸡和啤酒！！！', '270', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('235', '0', '', '', '猴年我愿送你蟠桃，赠你灵丹，举荐你为御马大仙，再赐你一双金睛火眼，祝福你洪福齐天，等你千古美名传!', '271', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('236', '0', '', '亲爱的朋友', '平安夜的每一朵雪花飘下，每一个烟花燃起，每一秒时间流动，每一份思念传送，都代表着我想要送给你的每一个祝福，祝可爱的你平安夜快乐，圣诞更快乐！ ', '272', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('237', '0', '', '亲爱的朋友', '平安夜的每一朵雪花飘下，每一个烟花燃起，每一秒时间流动，每一份思念传送，都代表着我想要送给你的每一个祝福，祝可爱的你平安夜快乐，圣诞更快乐！ ', '273', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('238', '0', '', '亲爱的朋友', '离家甚远，思念甚深，年岁渐长，父爱越深。你虽少言少语，爱意却丝毫不少，为家，为儿女，付出一辈子。父亲节到了，愿你健康快乐每一天。', '274', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('239', '0', '', '亲爱的朋友', '是最美的面孔，伴我拼搏争成功；是最美的笑容，伴我生命去追梦；是最美的祝福，伴我天涯向前冲。母亲节到了，愿亲爱的妈妈健康快乐永幸福。', '275', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('240', '0', '', '', '愚人节三从四德：从离得最近的下手，从关系最亲的下手，从脾气最好的下手；同学可以骗得，同事可以整得，密友可以逗得，恋人可以戏得。', '276', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('241', '0', '', '亲爱的朋友', '平平安安一把米，甜甜蜜蜜一颗枣，幸幸福福一片叶，缠缠绵绵一条线，快快乐乐包个粽，欢欢喜喜送给你，祝你健健康康一辈子！', '277', '', '', '', '', '', '', '', '60', '30', '60', '20', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('242', '0', '', '亲爱的朋友', '当你把目光洒在手机上，我的祝福就会在刹那间被你收藏，无论你在何方，我都希望你平安健康，中秋到了，祝你中秋团圆，合家欢乐！', '278', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('243', '0', '', '亲爱的朋友', '元旦来临访亲友，愿你事事顺心无烦忧；相聚时分喝杯酒，愿你好运天天有；往日情意再回首，暖暖祝福心中留，愿你开心久久，快乐永不休！', '279', '', '', '', '', '', '', '', '10', '20', '60', '50', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('244', '0', '', '亲爱的朋友', '世界上有很多东西藏不住，幸福或者爱情；世界上有很多东西无法去忘，回忆还有你。珍惜拥有，才能富足内心，幸福前行，我与你一起。祝你快乐！', '280', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('245', '0', '', '', '绿绿粽叶裹吉祥，填满真诚情意长，清清江水龙舟荡，淡淡黄酒解烦忧，端午节日又来到，幸福快乐绕身旁。祝端午节快乐开心，万事吉祥。', '281', '', '', '', '', '', '', '', '60', '20', '60', '30', '', '', '', '0', '12');
