INSERT INTO `ims_han_sheka_zhufu` VALUES ('252', '0', '', '', '阳光爽爽爽，龙舟闯闯闯，艾草长长长，粽子香香香，端午到到到，快乐笑笑笑，好运冒冒冒，成功罩罩罩，幸福闹闹闹，祝福妙妙妙，愿你好好好！', '288', '', '', '', '', '', '', '', '50', '15', '60', '50', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('253', '0', '', '', '阳光爽爽爽，龙舟闯闯闯，艾草长长长，粽子香香香，端午到到到，快乐笑笑笑，好运冒冒冒，成功罩罩罩，幸福闹闹闹，祝福妙妙妙，愿你好好好！', '289', '', '', '', '', '', '', '', '40', '22', '60', '50', '#fefcfc', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('254', '0', '', '', '圣诞到来花盛开，飘飘洒洒人间栽。圣诞树儿显风采，礼物多多树上开。结满如意吉祥果，幸福的生活在身边。愿你圣诞心愿现，快乐笑声永不断！', '290', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('255', '0', '', '', '　Wishing you all the blessings of a beautiful Christmas season.\r\n　　愿你拥有圣诞节所有美好的祝福。', '291', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('256', '0', '', '', '我一个人静静的坐在窗前,向远方的你送去一片深情:祝你中秋如意。有好吃的月饼吗?快带一点来,等不及了。', '292', '', '', '', '', '', '', '', '40', '18', '60', '50', '#fefcfc', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('257', '0', '', '', '不管在路边、海边、天边，好运一直陪在你身边；无论是过去、现在、将来，我会一直给你最暖的关怀，愿你成为世界上最开心最幸福的人！端午节快乐', '293', '', '', '', '', '', '', '', '35', '9', '35', '80', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('258', '0', '', '', ' 洁明皓月逢佳期，挚手共享两相依，爱深情浓甜似蜜，翱之清风何求兮。誓与君相伴月缺至月圆共度此时至此生!', '294', '', '', '', '', '', '', '', '66', '12', '80', '30', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('259', '0', '', '', '走走停停，欣赏美丽风景；寻寻觅觅，搜索快乐踪迹；欢欢喜喜，送走端午假期；忙忙碌碌，等待上班来临；点点滴滴，装满快乐心情。端午节后祝你开心', '295', '', '', '', '', '', '', '', '58', '20', '60', '50', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('260', '0', '', '', '颂一颂新年的美，放一响喜庆的炮，斟一杯美满的酒，奏一曲快乐的歌，描一幅富贵的画，绣一朵幸运的花，酿一句真诚的话：祝朋友元旦快乐！', '296', '', '', '', '', '', '', '', '20', '65', '30', '80', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('261', '0', '', '', '新年的钟声敲响，我的祝福不能迟到。祝你元旦无烦恼，心情无限好，妻贤子又孝，双亲不会老，事业步步高，生活更美妙，每天开口笑！', '297', '', '', '', '', '', '', '', '20', '55', '30', '80', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('262', '0', '', '', '新年，即将全场；气象，必是崭新；万事，吉祥如意；愁烦，远走他乡；问候，充满温馨；祝福，真挚万分；朋友，一生相伴；元旦，快乐无边！', '298', '', '', '', '', '', '', '', '48', '14', '75', '30', '#fefcfc', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('263', '0', '', '', '瑞雪纷飞喜迎门，新春来临骏马奔。驶入平安快乐道，淌过吉祥如意河。满载幸福团圆果，马鸣声声到君前。愿友春节一切好，接福纳财心欢笑。', '299', '', '', '', '', '', '', '', '71', '9', '80', '20', '#ffffff', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('264', '0', '', '', '红红的灯笼高高挂，满满的吉祥冉冉升，香香的饺子裹幸福，诚诚的祝福传安康，春节到，愿你福禄双全，合家美满，春节愉快！', '300', '', '', '', '', '', '', '', '65', '12', '80', '50', '#fefcfc', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('265', '0', '', '', '父爱如山，高大巍峨；父爱如天，粗旷深远；父爱如河，细长源源。父爱深邃、纯洁不图回报，父爱苦涩、难懂不可企及。父亲节，祝天下父母健康、平安！', '301', '', '', '', '', '', '', '', '70', '10', '85', '30', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('266', '0', '', '', '您的严厉是爱的表现，您的批评是爱的展示，您的教导是爱的基础，您的和蔼是爱的升华，父亲节到了，爸爸我爱您，愿您健康快乐，万事如意。', '302', '', '', '', '', '', '', '', '35', '55', '35', '65', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('267', '0', '', '', '您的严厉是爱的表现，您的批评是爱的展示，您的教导是爱的基础，您的和蔼是爱的升华，父亲节到了，爸爸我爱您，愿您健康快乐，万事如意。', '303', '', '', '', '', '', '', '', '70', '10', '60', '45', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('268', '0', '', '', '有一种关爱无微不至，有一种情感真诚无私，有一种形象堪比天使，有一种守望执着坚持，那就是母亲！母亲节到了，妈妈：节日快乐！', '304', '', '', '', '', '', '', '', '64', '10', '80', '50', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('269', '0', '', '', '有一种关爱无微不至，有一种情感真诚无私，有一种形象堪比天使，有一种守望执着坚持，那就是母亲！母亲节到了，妈妈：节日快乐！', '305', '', '', '', '', '', '', '', '60', '42', '50', '60', '#fefcfc', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('270', '0', '', '', '有一种关爱无微不至，有一种情感真诚无私，有一种形象堪比天使，有一种守望执着坚持，那就是母亲！母亲节到了，妈妈：节日快乐！', '306', '', '', '', '', '', '', '', '55', '40', '50', '60', '', '', '', '0', '12');
INSERT INTO `ims_han_sheka_zhufu` VALUES ('271', '0', '', '', '揽一把彩灯的绚烂，扯一缕庆贺的喜气，握一份团圆的情意，拥一点香甜的味道，为你送一句美丽的祝福，愿你元宵节快乐！', '307', '', '', '', '', '', '', '', '56', '10', '70', '50', '', '', '', '0', '12');
